import React from 'react';
import './style.less'
import Drag from '../../components/Drag'
import { useState, useEffect } from 'react';
// import ReactDOM from 'react-dom/client';

export default function Overview() {
  const moreClick = () => {
    console.log('tell me which num')
  }
  const clickPendingNum = () => {
    console.log('overview 点击待处理数量')
  }
  
  // currentOrder: 被拖拽元素的data-order, targetOrder: 目标元素的data-order
  const [sortArr, setSortArr] = useState([0,1,2,3])
  var sort1 = sortArr.slice() // 浅复制数组
  const getTargetOrder = (currentOrder, targetOrder) => {
    // console.log(currentOrder, targetOrder)
    if(currentOrder !== targetOrder){
    //   sortArr.forEach((item,index) => {
    //     if(currentOrder === item){
    //       sort1.splice(index, 1)
    //     }
    //     if(targetOrder === item){
    //       sort1.splice(index, 0, currentOrder)
    //       setSortArr(sort1)
    //     }
    //     console.log('sort1', sort1)
    //     return
    //   })
      for(var i = 0; i < sort1.length; i++){
        if(currentOrder === sort1[i]){
          sort1.splice(i, 1)
          i--
        }
        if(targetOrder === sort1[i]){
          sort1.splice(i, 0, currentOrder)
          setSortArr(sort1)
        }
        console.log('sort1', sort1)
        return 
      }
    }
    
  }

  const [optionOne] = useState({
    sizeOptions: ['small', 'middle', 'great', 'relax', 'giant'],
    title:'第0个dragDiv',
    getTargetOrder: getTargetOrder,
    "data-order": 0,
    childrenNode: () => {
      return (
        <>
          <div style={{height: '100px',width: '100px', backgroundColor:'red'}}></div>
        </>
      )
    }
  })
  const [optionTwo] = useState({
    sizeOptions: ['small', 'middle', 'great', 'relax', 'giant'],
    title:'第1个dragDiv',
    pendingNum: 2,
    clickPendingNum:clickPendingNum,
    moreClick: moreClick,
    getTargetOrder: getTargetOrder,
    "data-order": 1,
    childrenNode: () => {
      return (
        <>
          <div style={{height: '100px',width: '100px', backgroundColor:'orange'}}></div>
        </>
      )
    }
  })
  const [optionThree] = useState({
    sizeOptions: ['small', 'middle', 'great', 'relax', 'giant'],
    title:'第2个dragDiv',
    pendingNum: 3,
    clickPendingNum:clickPendingNum,
    getTargetOrder: getTargetOrder,
    "data-order": 2,
    childrenNode: () => {
      return (
        <>
          <div style={{height: '100px',width: '100px', backgroundColor:'blue'}}></div>
        </>
      )
    }
  })
  const [optionFour] = useState({
    sizeOptions: ['small', 'middle', 'great', 'relax', 'giant'],
    title:'第3个dragDiv',
    pendingNum: 4,
    clickPendingNum:clickPendingNum,
    getTargetOrder: getTargetOrder,
    "data-order": 3,
    childrenNode: () => {
      return (
        <>
          <div style={{height: '100px',width: '100px', backgroundColor:'green'}}></div>
        </>
      )
    }
  })
  const [componentsArr, setComponentsArr] = useState([]) 
  useEffect(() => {
    // console.log('sortArr改变了useEffect', sortArr)
    var oComponentArr = []
    sortArr.forEach((item1) => {
      [optionOne, optionTwo, optionThree, optionFour].forEach((item2) => {
        item2['data-order'] === item1 && oComponentArr.push(item2)
      })
    })
    // console.log('oComponentArr',oComponentArr)
    setComponentsArr(oComponentArr)
  }, [sortArr])// eslint-disable-line react-hooks/exhaustive-deps
 

  return (
    <div className="overview">
      {
          componentsArr.map((item, index) => {
              return <Drag key={index} options={item}>{item.childrenNode()}</Drag>
          })
      }
    </div>
  );
}
