/* 
 @params title:移动块的标题
 @params pendingNum:待处理数据的数量
 @params clickPendingNum:点击待处理数量触发的函数
 idea： 拖拽开始时，将原元素隐藏，克隆一个新元素跟随滚动，克隆元素跟随鼠标移动事件？？？？
*/

import React, { Component } from 'react';
import './style.less'

export default class Drag extends Component {
  constructor(props){
      super(props)
      this.state = {
        dragSize: props.options.sizeOptions[0], // 移动块的大小类型，小：small、中：middle、大：great、宽：relax、巨：giant
        sizeOptions: props.options.sizeOptions, // 父组件传递过来的大小型号的选项，默认第一个是初始型号
        allow_drag: false, // 是否允许拖拽
        old_ele: null, // 被拖拽的旧元素
        clone_ele: null, // 克隆旧元素的新元素
        origin_x: 0, // 被拖拽元素原始X坐标
        origin_y: 0, // 被拖拽元素原始Y坐标
      }
      this.mouseDown = this.mouseDown.bind(this)
      this.mouseUp = this.mouseUp.bind(this)
  }

  // 开始拖拽事件
  mouseDown(e){
    this.setState({
      allow_drag: true,
      old_ele: e.target.closest('.move_block'),
      clone_ele: e.target.closest('.move_block').cloneNode(true)
    },() => {
      console.log(typeof this.state.clone_ele, this.state.clone_ele)
    })
    
  }
  // 结束拖拽事件
  mouseUp(){
    this.setState({
      allow_drag: false
    },() => {
    })
  }

  render() {
    return (
      <div className={`blockAndBg ${this.state.dragSize}`}>
        <div className={`ortherItem ${!this.state.allow_drag ? "hide": ''}`}></div>
        <div className="move_block list-divs-big" style={this.state.allow_drag ? {opacity: 0}: null} draggable={this.state.allow_drag}>
          <div className="list-div-top">
              <span className="textcor" onClick={this.props.options.clickPendingNum}>
                {this.props.options.title} 
                {this.props.options.pendingNum ? "(" : null}<i className='clientNum'>{this.props.options.pendingNum}</i>{this.props.options.pendingNum ? ")" : null}
              </span>
              <div className="right_bet">
                  <span className="moveBtn" title="移动" onMouseDown={this.mouseDown} onMouseLeave={this.mouseUp}></span>
                  {this.props.options.moreClick ? <span className="moreClient" onClick={this.props.options.moreClick}>更多</span> : null}
                  <div className="install_border">
                      <div className="install_size"></div>
                      <div className="border_size visitors_size">
                          <div className={`small-line ${this.state.sizeOptions.indexOf("small") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                            this.setState({
                              dragSize: 'small'
                            })
                          }}>
                              <i className="iocsmall"></i>
                              <span>小</span>
                          </div>
                          <div className={`middle-line ${this.state.sizeOptions.indexOf("middle") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                            this.setState({
                              dragSize: 'middle'
                            })
                          }}>
                              <i className="iocmiddle"></i>
                              <span>中</span>
                          </div>
                          <div className={`great-line ${this.state.sizeOptions.indexOf("great") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                            this.setState({
                              dragSize: 'great'
                            })
                          }}>
                              <i className="iocgreat"></i>
                              <span>大</span>
                          </div>
                          <div className={`relax-line ${this.state.sizeOptions.indexOf("relax") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                            this.setState({
                              dragSize: 'relax'
                            })
                          }}>
                              <i className="iocrelax"></i>
                              <span>宽</span>
                          </div>
                          <div className={`giant-line ${this.state.sizeOptions.indexOf("giant") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                            this.setState({
                              dragSize: 'giant'
                            })
                          }}>
                              <i className="iocgiant"></i>
                              <span>巨</span>
                          </div>
                      </div>
                  </div>
              </div>
          </div>
          <div>{this.props.children}</div>
        </div>
        {this.state.allow_drag ? this.state.clone_ele : '没有返回'}
      </div>
    );
  }
}
