                    <i className="fa-angle-down"></i>
                </div>
                <ul className={this.state.isShowOptions ? "showOptions" : "hideOptions" + " mySelect-option"} >
                    {
                        this.state.mySelectOptions.map((item,index) => {
                            return(
                                <li key={index}>{item.label}</li>
                            )
                        })
                    }
                </ul>
            </div>
        );
    }
}
