import React, { useState, useEffect } from 'react';
import './style.less';
import * as area from '../../assets/js/data/areas';
import MySelect from '../../utils/MySelect/MySelect';

const Login = () => {

    // 函数组件是无状态组件，即没有state
    const [ loginName, setLoginName ] = useState('登录');
    const [ userName, setUserName] = useState("");
    const [ password, setPassword] = useState("");
    const [ pass, setPass] = useState("");
    const [ disableds, setDisableds] = useState(false);
    const [ areaSelect, setAreaSelect] = useState("");
    const [ tip, setTip] = useState("");
    const [ areas, setAreas] = useState(JSON.parse(JSON.stringify(area)).areas)
    const [currentPosition, setCurrentPosition] = useState(0)

    useEffect(() => {
        window.addEventListener('keydown', onKeyDown); // 添加全局事件
        return () => {
            window.removeEventListener('keydown', onKeyDown); // 销毁
        }
    }, [currentPosition])
    // 返回被选中项
    // const onChangeHandle = (res) => {
    //     console.log(res)
    // }

    // 键盘事件
    const onKeyDown = (e) => {
        // console.log(e.keyCode);
        switch(e.keyCode){
            case 13:
                this.loginBtn()
                break;
            default: 
                break;
        }
    };
    const loginBtn = () => {
        
    }
    return (
        <div className="main clearfix">
        <img src={require('../../assets/images/title.png')} alt="" className="login_logo" />
        <header className="header">营运协同系统</header>
        <div className="login-content">
            <img src={require('../../assets/images/login_bg.png')} className="login_bg" alt="" />
            <div className="login">
                <p className="login_p">账号</p>
                <input type="text" className="login_input" id="login_user" autoComplete="off" placeholder="请输入账号"/>
				<p className="login_p">密码：</p>
				<input type="password" className="login_input" id="login_psw" autoComplete="off" placeholder="请输入密码"/>
				<p className="login_p">区域：</p>
				<div className="mySelect" id="login_area">
                    {/* <MySelect options={areas} onChange={onChangeHandle}></MySelect> */}
                    <MySelect options={areas} ></MySelect>
                </div>
				<p className="login_tip"></p>
				<button id="login_btn" >{loginName}</button>
				<div className="login_version">版本号：V4.4</div>
            </div>
        </div>
    </div>
    );
}
export default Login
