/* 
 @params title:移动块的标题
 @params pendingNum:待处理数据的数量
 @params clickPendingNum:点击待处理数量触发的函数
*/

import React, { Component } from 'react';
import './style.less'

export default class Drag extends Component {
  constructor(props){
      super(props)
      this.state = {
        dragSize: props.options.sizeOptions[0], // 移动块的大小类型，小：small、中：middle、大：great、宽：relax、巨：giant
        sizeOptions: props.options.sizeOptions, // 父组件传递过来的大小型号的选项，默认第一个是初始型号
        allow_drag: false, // 是否允许拖拽
        origin_x: 0, // 被拖拽元素原始X坐标
        origin_y: 0, // 被拖拽元素原始Y坐标
      }
      this.mouseDown = this.mouseDown.bind(this)
      this.mouseUp = this.mouseUp.bind(this)
  }

  // 开始拖拽事件
  mouseDown(){
    this.setState({
      allow_drag: true
    },() => {

    })
  }
  // 结束拖拽事件
  mouseUp(){
    this.setState({
      allow_drag: false
    },() => {
      console.log(this.state.allow_drag)
    })
  }
  getPosition(node) {
    //获取元素相对于其父元素的left值var left
    var left = node.offsetLeft; 
    var top = node.offsetTop;
    // 取得元素的offsetParent
    current = node.offsetParent; 
    
  　 // 一直循环直到根元素
    while(current != null) {　　
        left += current.offsetLeft;　　
        top += current.offsetTop;　　
        current = current.offsetParent;　
    }
    return {
        "left": left,
        "top": top
    };
  }
  getElementPos(elementId) {
    var ua = navigator.userAgent.toLowerCase();
    var isOpera = (ua.indexOf('opera') != -1);
    var isIE = (ua.indexOf('msie') != -1 && !isOpera); // not opera spoof
    var el = document.getElementById(elementId);
    if(el.parentNode === null || el.style.display == 'none') {
      return false;
    }     
    var parent = null;
    var pos = [];    
    var box;    
    if(el.getBoundingClientRect){         
     box = el.getBoundingClientRect();
     var scrollTop = Math.max(document.documentElement.scrollTop, document.body.scrollTop);
     var scrollLeft = Math.max(document.documentElement.scrollLeft, document.body.scrollLeft);
     return {x:box.left + scrollLeft, y:box.top + scrollTop};
    }else if(document.getBoxObjectFor)    // gecko   
    {
     box = document.getBoxObjectFor(el);
     var borderLeft = (el.style.borderLeftWidth)?parseInt(el.style.borderLeftWidth):0;
     var borderTop = (el.style.borderTopWidth)?parseInt(el.style.borderTopWidth):0;
     pos = [box.x - borderLeft, box.y - borderTop];
    } else    // safari & opera   
    {
     pos = [el.offsetLeft, el.offsetTop]; 
     parent = el.offsetParent;    
     if (parent != el) {
      while (parent) { 
       pos[0] += parent.offsetLeft;
       pos[1] += parent.offsetTop;
       parent = parent.offsetParent;
      } 
     }  
     if (ua.indexOf('opera') != -1 || ( ua.indexOf('safari') != -1 && el.style.position == 'absolute' )) {
      pos[0] -= document.body.offsetLeft;
      pos[1] -= document.body.offsetTop;        
     }   
    }             
    if (el.parentNode) {
       parent = el.parentNode;
      } else {
       parent = null;
      }
    while (parent && parent.tagName != 'BODY' && parent.tagName != 'HTML') { // account for any scrolled ancestors
     pos[0] -= parent.scrollLeft;
     pos[1] -= parent.scrollTop;
     if (parent.parentNode) {
      parent = parent.parentNode;
     } else {
      parent = null;
     }
    }
    return {x:pos[0], y:pos[1]};
   }

  render() {
    return (
      <div className={`blockAndBg ${this.state.dragSize}`}>
        <div className={`ortherItem ${!this.state.allow_drag ? "hide": ''}`}></div>
        <div className={ `move_block list-divs-big ${this.state.allow_drag ? "drag_position": ""}`} draggable={this.state.allow_drag}>
          <div className="list-div-top">
              <span className="textcor" onClick={this.props.options.clickPendingNum}>
                {this.props.options.title} 
                {this.props.options.pendingNum ? "(" : null}<i className='clientNum'>{this.props.options.pendingNum}</i>{this.props.options.pendingNum ? ")" : null}
              </span>
              <div className="right_bet">
                  <span className="moveBtn" title="移动" onMouseDown={this.mouseDown} onMouseLeave={this.mouseUp}></span>
                  {this.props.options.moreClick ? <span className="moreClient" onClick={this.props.options.moreClick}>更多</span> : null}
                  <div className="install_border">
                      <div className="install_size"></div>
                      <div className="border_size visitors_size">
                          <div className={`small-line ${this.state.sizeOptions.indexOf("small") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                            this.setState({
                              dragSize: 'small'
                            })
                          }}>
                              <i className="iocsmall"></i>
                              <span>小</span>
                          </div>
                          <div className={`middle-line ${this.state.sizeOptions.indexOf("middle") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                            this.setState({
                              dragSize: 'middle'
                            })
                          }}>
                              <i className="iocmiddle"></i>
                              <span>中</span>
                          </div>
                          <div className={`great-line ${this.state.sizeOptions.indexOf("great") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                            this.setState({
                              dragSize: 'great'
                            })
                          }}>
                              <i className="iocgreat"></i>
                              <span>大</span>
                          </div>
                          <div className={`relax-line ${this.state.sizeOptions.indexOf("relax") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                            this.setState({
                              dragSize: 'relax'
                            })
                          }}>
                              <i className="iocrelax"></i>
                              <span>宽</span>
                          </div>
                          <div className={`giant-line ${this.state.sizeOptions.indexOf("giant") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                            this.setState({
                              dragSize: 'giant'
                            })
                          }}>
                              <i className="iocgiant"></i>
                              <span>巨</span>
                          </div>
                      </div>
                  </div>
              </div>
          </div>
          <div>{this.props.children}</div>
        </div>
      </div>
    );
  }
}
