import React, { Component } from 'react';
import './style.less';
import * as area from '../../assets/js/data/areas';
import MySelect from '../../utils/MySelect/MySelect';

export default class Login extends Component {
    constructor(props){
        super(props)
        this.state = {
            loginName: '登录',
            userName: '',
            password: '',
            pass: '',
            disableds: false,
            tip: '',
            areas: JSON.parse(JSON.stringify(area)).areas,
            areaNum: ''
        }
    }
    onChangeHandle = (res) =>{
        this.setState({
            areaNum: res.value
        })
    }
    componentDidMount(){ 
        //组件挂载时候，注册keypress事件
        document.addEventListener('keypress',this.onkeydown)
    }
    componentWillUmount(){
       //组件卸载时候，注销keypress事件
       document.removeEventListener("keypress",this.onkeydown)
    }
    onkeydown = (e) => {
        if (e.keyCode === 13) {
            this.loginBtn()
        }
    }
