		// 		this.tip="用户名为4-30位数字、字母、@字符,区分大小写";
		// 		this.loginName="登录";
		// 		this.disableds=false;
		// 		return;
		// 	}else{
		// 		this.tip="";
		// 	}
		// 	if(isUserName(this.userName)===false){
		// 		this.loginName="登录";
		// 		this.disableds=false;
		// 		return;
		// 	}
		// 	if(this.pass==""){
		// 		this.loginName="登录";
		// 		this.disableds=false;
		// 		return;
		// 	}
		// 	if(this.pass.length<6||this.pass.length>30){
		// 		this.tip="密码为6-30位数字、字母、字符,区分大小写";
		// 		this.loginName="登录";
