import React, { Component } from 'react';
import './style.less'

export default class Drag extends Component {
  constructor(props){
      super(props)
      this.state = {
        dragSize: props.sizeOptions[0], // 移动块的大小类型，小：small、中：middle、大：great、宽：relax、巨：giant
        sizeOptions: props.sizeOptions, // 父组件传递过来的大小型号的选项，默认第一个是初始型号
        sizeClass: 'small'
      }
      this.smallHandle = this.smallHandle.bind(this)
      this.middleHandle = this.middleHandle.bind(this)
      this.greatHandle = this.greatHandle.bind(this)
      this.relaxHandle = this.relaxHandle.bind(this)
      this.giantHandle = this.giantHandle.bind(this)
  }
  componentDidMount(){
    if(this.state.dragSize === "small"){
      this.setState({
        sizeClass: 'smallBlock'
      })
    }else if(this.state.dragSize === "middle"){
      this.setState({
        sizeClass: 'middleBlock'
      })
    }else if(this.state.dragSize === "great"){
      this.setState({
        sizeClass: 'greatBlock'
      })
    }else if(this.state.dragSize === "relax"){
      this.setState({
        sizeClass: 'relaxBlock'
      })
    }else if(this.state.dragSize === "giant"){
      this.setState({
        sizeClass: 'giantBlock'
      })
    }
  }
  smallHandle(){
    this.setState({
      sizeClass: 'smallBlock'
    })
  }
  middleHandle(){
    this.setState({
      sizeClass: 'middleBlock'
    })
  }
  greatHandle(){
    this.setState({
      sizeClass: 'greatBlock'
    })
  }
  relaxHandle(){
    this.setState({
      sizeClass: 'relaxBlock'
    })
  }
  giantHandle(){
    this.setState({
      sizeClass: 'giantBlock'
    })
  }
  render() {
    return (
      <div className={ `move_block list-divs-big ${this.state.sizeClass}`}>
        <div className="list-div-top">
            <span className="textcor">{this.props.options.title}（<i className="clientNum"></i>）</span>
            <div className="right_bet">
                <span className="moveBtn" title="移动"></span>
                <span className="moreClient">更多</span>
                <div className="install_border">
                    <div className="install_size"></div>
                    <div className="border_size visitors_size">
                        <div className={`small ${this.state.sizeOptions.indexOf("small") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                          this.setState({
                            sizeClass: 'smallBlock'
                          })
                        }}>
                            <i className="iocsmall"></i>
                            <span>小</span>
                        </div>
                        <div className={`middle ${this.state.sizeOptions.indexOf("middle") !== -1 ? 'show-flex' : 'hide'}`}>
                            <i className="iocmiddle"></i>
                            <span>中</span>
                        </div>
                        <div className={`great ${this.state.sizeOptions.indexOf("great") !== -1 ? 'show-flex' : 'hide'}`}>
                            <i className="iocgreat"></i>
                            <span>大</span>
                        </div>
                        <div className={`relax ${this.state.sizeOptions.indexOf("relax") !== -1 ? 'show-flex' : 'hide'}`}>
                            <i className="iocrelax"></i>
                            <span>宽</span>
                        </div>
                        <div className={`giant ${this.state.sizeOptions.indexOf("giant") !== -1 ? 'show-flex' : 'hide'}`}>
                            <i className="iocgiant"></i>
                            <span>巨</span>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        <div>{this.props.children}</div>
      </div>
    );
  }
}
