import React, { Component } from 'react';
import './style.less'

export default class MySelect extends Component {
    constructor(props){
        super(props)
        this.state = {
            mySelectOptions: props.options, // 下拉选项
            isShowOptions: false, // 是否显示下拉款
            selectedLabel: '', // 被选中项的label
            liWasSelected: false, // 被选中项的样式
        }
        this.initOptions = this.initOptions.bind(this)
        this.blurInput = this.blurInput.bind(this)
        this.selected = this.selected.bind(this)
        this.refreshOption = this.refreshOption.bind(this)
    }
    
    // 初始化下拉框
    initOptions(){
        // this.refs.inputWrap.childNodes[0]
        this.setState({
            selectedLabel: '',
            isShowOptions: true
        })
    }
    // 当被选框失去焦点时
    blurInput(){
        this.setState({
            isShowOptions: false
        })
    }
    // 选项被选中时
    selected(e){
        this.setState({
            selectedLabel: e.target.innerText,
            isShowOptions: false
        })
    }
    // 更新下拉选项，传两个参数options：新的选项数组，value：初始化的value值（可不传）
    refreshOption(){
        console.log(this.props.refreshOption === undefined)
    }
    render() {
        return (
            <div className="my-select" tabIndex="0" onBlur={this.blurInput}>
                <div className="inputWrap" ref="inputWrap" onClick={this.initOptions}>
                    <p>{this.state.selectedLabel}</p>
                    <i className={this.state.isShowOptions ? "fa-angle-up" : "fa-angle-down"}></i>
                </div>
                <ul ref="mySelectOption" className={`mySelect-option ${this.state.isShowOptions ? "showOptions" : "hideOptions"}` } >
                    {
                        this.state.mySelectOptions.map((item,index) => {
                            return(
                                <li key={item.value} onClick={(e) => this.selected(e)} className={this.state.liWasSelected ? 'isSelected' : '' }>{item.label}</li>
                            )
                        })
                    }
                </ul>
            </div>
        );
    }
}
