/* 
 @params title:移动块的标题
 @params pendingNum:待处理数据的数量
 @params clickPendingNum:点击待处理数量触发的函数
*/

import React, { Component } from 'react';
import './style.less'

export default class Drag extends Component {
  constructor(props){
      super(props)
      this.state = {
        dragSize: props.options.sizeOptions[0], // 移动块的大小类型，小：small、中：middle、大：great、宽：relax、巨：giant
        sizeOptions: props.options.sizeOptions, // 父组件传递过来的大小型号的选项，默认第一个是初始型号
        sizeClass: 'small',
        allow_drag: false, // 是否允许拖拽
      }
  }
  componentDidMount(){
    // 初始化移动块的大小
    if(this.state.dragSize === "small"){
      this.setState({
        sizeClass: 'smallBlock'
      })
    }else if(this.state.dragSize === "middle"){
      this.setState({
        sizeClass: 'middleBlock'
      })
    }else if(this.state.dragSize === "great"){
      this.setState({
        sizeClass: 'greatBlock'
      })
    }else if(this.state.dragSize === "relax"){
      this.setState({
        sizeClass: 'relaxBlock'
      })
    }else if(this.state.dragSize === "giant"){
      this.setState({
        sizeClass: 'giantBlock'
      })
    }
  }
  render() {
    return (
      <div className={ `move_block list-divs-big ${this.state.sizeClass}`} draggable={this.state.allow_drag}>
        <div className="list-div-top">
            <span className="textcor" onClick={this.props.options.clickPendingNum}>
              {this.props.options.title} 
              {this.props.options.pendingNum ? "(" : null}<i className='clientNum'>{this.props.options.pendingNum}</i>{this.props.options.pendingNum ? ")" : null}
            </span>
            <div className="right_bet">
                <span className="moveBtn" title="移动" onMouseDown={() => this.setState({allow_drag})}></span>
                {this.props.options.moreClick ? <span className="moreClient" onClick={this.props.options.moreClick}>更多</span> : null}
                <div className="install_border">
                    <div className="install_size"></div>
                    <div className="border_size visitors_size">
                        <div className={`small ${this.state.sizeOptions.indexOf("small") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                          this.setState({
                            sizeClass: 'smallBlock'
                          })
                        }}>
                            <i className="iocsmall"></i>
                            <span>小</span>
                        </div>
                        <div className={`middle ${this.state.sizeOptions.indexOf("middle") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                          this.setState({
                            sizeClass: 'middleBlock'
                          })
                        }}>
                            <i className="iocmiddle"></i>
                            <span>中</span>
                        </div>
                        <div className={`great ${this.state.sizeOptions.indexOf("great") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                          this.setState({
                            sizeClass: 'greatBlock'
                          })
                        }}>
                            <i className="iocgreat"></i>
                            <span>大</span>
                        </div>
                        <div className={`relax ${this.state.sizeOptions.indexOf("relax") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                          this.setState({
                            sizeClass: 'relaxBlock'
                          })
                        }}>
                            <i className="iocrelax"></i>
                            <span>宽</span>
                        </div>
                        <div className={`giant ${this.state.sizeOptions.indexOf("giant") !== -1 ? 'show-flex' : 'hide'}`} onClick={() => {
                          this.setState({
                            sizeClass: 'giantBlock'
                          })
                        }}>
                            <i className="iocgiant"></i>
                            <span>巨</span>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        <div>{this.props.children}</div>
      </div>
    );
  }
}
