import React, { Component } from 'react';
import './style.less';
import * as area from '../../assets/js/data/areas';
import MySelect from '../../utils/MySelect/MySelect';

export default class Login extends Component {
    constructor(props){
        super(props)
        this.state = {
            loginName: '登录',
            userName: '',
            password: '',
            pass: '',
            disableds: false,
            tip: '',
            areas: JSON.parse(JSON.stringify(area)).areas,
        }
    }
    componentDidMount(){ 
        //组件挂载时候，注册keypress事件
        document.addEventListener('keypress',this.onkeydown)
    }
    componentWillUmount(){
       //组件卸载时候，注销keypress事件
       document.removeEventListener("keypress",this.onkeydown)
    }
    onkeydown = (e) => {
        if (e.keyCode === 13) {
            this.loginBtn()
        }
    }
    loginBtn = () => {
        this.state.pass=this.state.password;
			var userLength=this.userName.length;
			userLength=userLength <= 9 ? "0" + userLength : userLength;
			var areaNum=this.areaSelect.getResult().value;
			this.loginName="登录中...";
			this.disableds=true;
			if(this.userName==""){
				this.loginName="登录";
				this.disableds=false;
				return ;
			}
			if(this.userName.length<4||this.userName.length>30){
				this.tip="用户名为4-30位数字、字母、@字符,区分大小写";
				this.loginName="登录";
				this.disableds=false;
				return;
			}else{
				this.tip="";
			}
			if(isUserName(this.userName)===false){
				this.loginName="登录";
				this.disableds=false;
				return;
			}
			if(this.pass==""){
				this.loginName="登录";
				this.disableds=false;
				return;
			}
			if(this.pass.length<6||this.pass.length>30){
				this.tip="密码为6-30位数字、字母、字符,区分大小写";
				this.loginName="登录";
				this.disableds=false;
				return;
			}else{
				this.tip="";
			}
			if(isPasswdOne(this.pass)==false){
				this.loginName="登录";
				this.disableds=false;
				return;
			}
    }
    render() {
        return (
            <div className="main clearfix">
                <img src={require('../../assets/images/title.png')} alt="" className="login_logo" />
                <header className="header">营运协同系统</header>
                <div className="login-content">
                    <img src={require('../../assets/images/login_bg.png')} className="login_bg" alt="" />
                    <div className="login">
                        <p className="login_p">账号</p>
                        <input type="text" className="login_input" id="login_user" autoComplete="off" placeholder="请输入账号"/>
                        <p className="login_p">密码：</p>
                        <input type="password" className="login_input" id="login_psw" autoComplete="off" placeholder="请输入密码"/>
                        <p className="login_p">区域：</p>
                        <div className="mySelect" id="login_area">
                            {/* <MySelect options={this.state.areas} onChange={onChangeHandle}></MySelect> */}
                            <MySelect options={this.state.areas} ></MySelect>
                        </div>
                        <p className="login_tip"></p>
                        <button id="login_btn" onClick={this.loginBtn} disabled={this.state.disableds}>{this.state.loginName}</button>
                        <div className="login_version">版本号：V4.4</div>
                    </div>
                </div>
            </div>
        );
    }
}
