        // })
    }
  return (
    <div className="main clearfix">
                <img src={require('../../assets/images/title.png')} alt="" className="login_logo" />
                <header className="header">营运协同系统</header>
                <div className="login-content">
                    <div className="sad"></div>
                    <img src={require('../../assets/images/login_bg.png')} className="login_bg" alt="" />
                    <div className="login">
                        <p className="login_p">账号</p>
                        <input type="text" className="login_input" id="login_user" autoComplete="off" placeholder="请输入账号" onInput={userNameInput}/>
                        <p className="login_p">密码：</p>
                        <input type="password" className="login_input" id="login_psw" autoComplete="off" placeholder="请输入密码" onInput={passWordInput}/>
                        <p className="login_p">区域：</p>
                        <div className="mySelect" id="login_area">
                            {/* <MySelect options={this.state.areas} onChange={onChangeHandle}></MySelect> */}
                            <MySelect onRef={onRef} options={areas}></MySelect>
                        </div>
                        <p className="login_tip">{tip}</p>
                        <button id="login_btn" onClick={loginBtn} disabled={disableds}>{loginName}</button>
                        <div className="login_version">版本号：V4.4</div>
                    </div>
                </div>
            </div>
  );
}
export default Login
